def RNN(layerType=3, dropoutNum=0.6):
    inputs = Input(name='inputs',shape=[max_len])
    layer = Embedding(max_words,50,input_length=max_len)(inputs)
    layer = LSTM(64)(layer)
    #layer = Dense(256,name='FC1')(layer)
    # CHANGE -- 2 dense layers instead of 1
    if(layerType == 1):
        layer = Dense(128,name='FC1')(layer)
        layer = Activation('relu')(layer)
    elif(layerType == 2):
        layer = Dense(256,name='FC1')(layer)
        layer = Activation('relu')(layer)
    elif(layerType == 3):
        layer = Dense(128,name='FC1')(layer)
        layer = Activation('relu')(layer)
        #layer = Dropout(dropoutNum)(layer)
        layer = Dense(128,name='FC2')(layer)
        layer = Activation('relu')(layer)
    layer = Dropout(dropoutNum)(layer) # this seems pretty high -- hyperparameter to tweak
    layer = Dense(num_classes,name='out_layer',activation='softmax')(layer)
    #layer = Dense(1,name='out_layer',activation='softmax')(layer)
    #layer = Activation('sigmoid')(layer)
    model = Model(inputs=inputs,outputs=layer)
    return model

